sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
      return;
   }

	var getEmailAddr = new GlideAjax('GetEmailAddress');
	getEmailAddr.addParam('sysparm_name','getEmail');
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	getEmailAddr.getXML(returnedEmail);

	function returnedEmail(response){
		var email = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',email);
		g_form.addInfoMessage("Email address was updated from the user table: "+email);
	}
	if(typeof gel !== 'undefined'){
		try{
			var priorityElement = gel("incident.u_requested_for_email");
			setPriorityBackgroundColor(priorityElement);
		}catch(ex){
			console.warn('Unable to do this!');
		}
	}
}
